document.addEventListener("click",function(event){let target=event.target.nodeName==="SPAN"||event.target.nodeName==="path"||event.target.nodeName==="svg"?event.target.parentElement:event.target;if(!target.classList.contains("accordion-toggle"))return;var content=document.querySelector(target.dataset.target);if(!content)return;if(content.classList.contains("active")){content.classList.remove("active");target.lastElementChild.classList.add("accordion-arrow");target.lastElementChild.classList.remove("accordion-arrow-180");return}var accordions=document.querySelectorAll(".accordion-content.active");for(var i=0;i<accordions.length;i++){accordions[i].classList.remove("active");target.lastElementChild.classList.remove("accordion-arrow-180");target.lastElementChild.classList.add("accordion-arrow");target.lastElementChild.classList.remove("accordion-arrow-180")}content.classList.toggle("active");if(target.lastElementChild.nodeName==="svg"){target.lastElementChild.classList.add("accordion-arrow-180");target.lastElementChild.classList.remove("accordion-arrow")}});